mapper.select:
SELECT COUNT(*) FROM ftArticle AS A INNER JOIN football_stadium AS f ON A.fsid = f.id WHERE 1
AND A.boardId = #{boardId}
AND f.stadiumName LIKE CONCAT('%', #{searchKeyword}, '%')
AND f.area = #{area}

SELECT A.*, f.stadiumName, f.area, f.address, f.img
FROM FtArticle AS A
INNER JOIN football_stadium AS f ON A.fsid = f.id
WHERE 1

    AND A.boardId = #{boardId}


    AND f.stadiumName LIKE CONCAT('%', #{searchKeyword}, '%')


    AND f.area = #{area}

ORDER BY A.id DESC

    LIMIT #{limitFrom}, #{limitTake}
