mapper.select:
SELECT EXISTS (
    SELECT 1 FROM member_like WHERE fromMemberId = #{fromId} AND toMemberId = #{toId}
)

SELECT COUNT(*)
FROM member_like
WHERE fromMemberId = #{fromMemberId}
  AND toMemberId = #{toMemberId}

mapper.update:
UPDATE member
SET profileImg = #{profileImgPath}
WHERE id = #{memberId}
