mapper.select:
SELECT *
FROM team
WHERE 1 = 1




            AND teamName LIKE CONCAT('%', #{searchKeyword}, '%')


            AND teamLeader LIKE CONCAT('%', #{searchKeyword}, '%')






    AND area = #{area}



    AND avgLevel = #{avgLevel}

ORDER BY id DESC
LIMIT #{limitFrom}, #{limitTake}

SELECT COUNT(*) FROM team
WHERE 1 = 1



            AND teamName LIKE CONCAT('%', #{searchKeyword}, '%')


            AND teamLeader LIKE CONCAT('%', #{searchKeyword}, '%')




    AND area = #{area}


    AND teamRank = #{avgLevel}
